mapper.select:
select count(1) from zs_machine_info zmi LEFT JOIN zs_machine_type_info zmti ON zmi.`machine_type_id` = zmti.`id`
and zmti.parent_id = #{machineTypeId,jdbcType=INTEGER}
and zmi.machine_id = #{machineId,jdbcType=INTEGER}
and zmi.department_id = #{departmentId, jdbcType=INTEGER}
and zmi.usage_state = 0
and zmi.usage_state = 1
and (zmi.usage_state = 0 or zmi.usage_state = 2)
and zmi.status = 0
ORDER BY zmti.`parent_id`, FIELD(zmi.usage_state,2,0,1), zmi.create_time

select zmi.machine_id, zmi.`name`, zmi.machine_model, zmi.machine_num, zmi.machine_no, zmi.department_id, zmi.usage_state, zmi.remark, zmi.create_time, zmi.`status`, zdi.name as department_name from zs_machine_info zmi left join zs_department_info zdi on zmi.department_id = zdi.department_id LEFT JOIN zs_machine_type_info zmti ON zmi.`machine_type_id` = zmti.`id`
and zmti.parent_id = #{machineTypeId,jdbcType=INTEGER}
and zmi.machine_id = #{machineId,jdbcType=INTEGER}
and zmi.department_id = #{departmentId, jdbcType=INTEGER}
and zmi.usage_state = 0
and zmi.usage_state = 1
and (zmi.usage_state = 0 or zmi.usage_state = 2)
and zmi.status = 0
ORDER BY zmti.`parent_id`, FIELD(zmi.usage_state,2,0,1), zmi.create_time
limit #{startLine},#{pageSize}

mapper.insert:
insert into zs_machine_info (machine_id, `name`, machine_model, 
  machine_num, machine_no, department_id, usage_state,
  remark, create_time, `status`
  )
values (#{machineId,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{machineModel,jdbcType=VARCHAR}, 
  #{machineNum,jdbcType=VARCHAR}, #{machineNo,jdbcType=VARCHAR}, #{departmentId,jdbcType=INTEGER}, #{usageState,jdbcType=INTEGER},
  #{remark,jdbcType=VARCHAR}, #{createTime,jdbcType=VARCHAR}, #{status,jdbcType=INTEGER}
  )
