mapper.select:
select
zmi.manager_id, zmi.account, zmi.`password`, zmi.`name`, zmi.phone, zmi.post, zmi.department_id, zmi.sex, zmi.`role`, zmi.login_time,
zmi.create_time, zmi.`status`, zdi.name as department_name
from zs_manager_info zmi
left join zs_department_info zdi on zmi.department_id = zdi.department_id
where zmi.status = 0

    and zmi.manager_id = #{managerId,jdbcType=INTEGER}


    and zmi.`role` = #{role,jdbcType=INTEGER}


    and (zmi.`role` = 1 or zmi.`role` = 2)


    limit #{startLine},#{pageSize}

select count(1) from zs_manager_info zmi left join zs_department_info zdi on zmi.department_id = zdi.department_id where zmi.status = 0
and zmi.manager_id = #{managerId,jdbcType=INTEGER}
and zmi.`role` = #{role,jdbcType=INTEGER}
and (zmi.`role` = 1 or zmi.`role` = 2)

mapper.insert:
insert into zs_manager_info (manager_id, account, `password`, 
  `name`, phone, post, 
  department_id, sex, `role`,
  login_time, create_time, `status`
  )
values (#{managerId,jdbcType=INTEGER}, #{account,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, 
  #{name,jdbcType=VARCHAR}, #{phone,jdbcType=VARCHAR}, #{post,jdbcType=VARCHAR}, 
  #{departmentId,jdbcType=INTEGER}, #{sex,jdbcType=INTEGER}, #{role,jdbcType=INTEGER},
  #{loginTime,jdbcType=VARCHAR}, #{createTime,jdbcType=VARCHAR}, #{status,jdbcType=INTEGER}
  )
