mapper.select:
SELECT zri.id AS id,zri.MAC AS mac,zri.IP AS ip,zri.linkstatus AS linkstatus,zri.remark AS remark,zmti.`name` AS repeaterNum,zmti2.`name` AS repeaterName,zdi.`name` AS departmentName, zri2.`name` AS roomName FROM zs_repeater_info zri LEFT JOIN zs_machine_type_info zmti ON zri.`machine_type_id` = zmti.`id` LEFT JOIN zs_machine_type_info zmti2 ON zmti.`parent_id` = zmti2.`id` LEFT JOIN zs_department_info zdi ON zri.`department_id` = zdi.`department_id` LEFT JOIN zs_room_info zri2 ON zri2.`room_id` = zri.`room_id`
zri.status = 0
and zri.id = #{id, jdbcType=INTEGER}
and zri.department_id = #{departmentId, jdbcType=INTEGER}
limit #{startLine},#{pageSize}

select
count(1)
from zs_repeater_info

    status = 0

        and id = #{id, jdbcType=INTEGER}


        and department_id = #{departmentId, jdbcType=INTEGER}
